changeSet.sql:
GRANT USAGE ON SCHEMA auth TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA auth GRANT USAGE ON SEQUENCES TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA auth GRANT EXECUTE ON FUNCTIONS TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA auth GRANT SELECT,INSERT,UPDATE ON TABLES TO termserver_app;

GRANT USAGE ON SCHEMA auth TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA auth GRANT EXECUTE ON FUNCTIONS TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA auth GRANT SELECT ON TABLES TO termserver_viewer;

select core.set_schema_search_path('termserver_admin', 'auth', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_app', 'auth', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_viewer', 'auth', p_action := 'BEGIN');
