changeSet.sql:
GRANT USAGE ON SCHEMA core TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA core GRANT USAGE ON SEQUENCES TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA core GRANT EXECUTE ON FUNCTIONS TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA core GRANT SELECT,INSERT,UPDATE ON TABLES TO termserver_app;

GRANT USAGE ON SCHEMA core TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA core GRANT EXECUTE ON FUNCTIONS TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA core GRANT SELECT ON TABLES TO termserver_viewer;

select core.set_schema_search_path('termserver_admin', 'core', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_app', 'core', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_viewer', 'core', p_action := 'BEGIN');
