changeSet.sql:
GRANT USAGE ON SCHEMA job TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA job GRANT USAGE ON SEQUENCES TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA job GRANT EXECUTE ON FUNCTIONS TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA job GRANT SELECT,INSERT,UPDATE ON TABLES TO termserver_app;

GRANT USAGE ON SCHEMA job TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA job GRANT EXECUTE ON FUNCTIONS TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA job GRANT SELECT ON TABLES TO termserver_viewer;

select core.set_schema_search_path('termserver_admin', 'job', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_app', 'job', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_viewer', 'job', p_action := 'BEGIN');
