changeSet.sql:
GRANT USAGE ON SCHEMA terminology TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA terminology GRANT USAGE ON SEQUENCES TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA terminology GRANT EXECUTE ON FUNCTIONS TO termserver_app;
ALTER DEFAULT PRIVILEGES IN SCHEMA terminology GRANT SELECT,INSERT,UPDATE ON TABLES TO termserver_app;

GRANT USAGE ON SCHEMA terminology TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA terminology GRANT EXECUTE ON FUNCTIONS TO termserver_viewer;
ALTER DEFAULT PRIVILEGES IN SCHEMA terminology GRANT SELECT ON TABLES TO termserver_viewer;

select core.set_schema_search_path('termserver_admin', 'terminology', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_app', 'terminology', p_action := 'BEGIN');
select core.set_schema_search_path('termserver_viewer', 'terminology', p_action := 'BEGIN');
